    content = content + columns.getColumnValue(i) + ";";
} 


if(globalMap.containsKey("content")){
	globalMap.put("content", ((String)globalMap.get("content"))+"\n"+content);
}else{
	for(int i=0; i < columns.getColumnCount(); i++){
		DynamicMetadata columnMetadata = columns.getColumnMetadata(i);
		header = header + columnMetadata.getName() + ";";
	}
	globalMap.put("content",header +"\n"+content);
}

