Dynamic columns = row11.values;
String file = ((String)globalMap.get("tFileList_5_CURRENT_FILEPATH"));
String nomefile = (String)globalMap.get("tFileList_5_CURRENT_FILE");
String nometabella = (String)globalMap.get(nomefile);

String[] campi = ((String)globalMap.get(file)).split(",");

String nomicampi = "";
String values = "";

for (int i = 0; i < columns.getColumnCount(); i++) {  
	System.out.println(campi[i] + " " + columns.getColumnValue(i));
	nomicampi = nomicampi + campi[i].split("\\|")[0] + ",";
	switch(campi[i].split("\\|")[1]){
		case "string":
			values = values + "'" +columns.getColumnValue(i) + "',";
			break;

		case "date":
			String date = TalendDate.formatDate("yyyy-MM-dd", TalendDate.parseDate("dd/MM/YYYY", (String)columns.getColumnValue(i)));
			values = values + "'" +date + "',";
			break;

		case "number":
			values = values +columns.getColumnValue(i) + ",";
			break;

	}
} 



output_row.query = "INSERT INTO " + nometabella +"(" +nomicampi.substring(0,nomicampi.length()-1) + ")"+"VALUES ("+ values.substring(0,values.length()-1) +")";

